y small.readmore a, \n	body.search article.sticky small.readmore a{\n		color: #FF9EF5; /*@brand-complementary: 	#FF9EF5;*/\n	}\n\n/************* footer.less */\n\nfooter#pageFooter{\n	background-color: #eee;\n}\n	footer#pageFooter a{\n		color: #FF3CDE;\n	}\n\n/************* form.less */\n\nbutton:not([type=\"image\"]), input[type=\"submit\"]:not([type=\"image\"]),\n.btn-default,\n.form-submit input{\n	background-color: #FF3CDE;\n  	border: 1px solid #FF3CDE;\n	color: #FFFFFF;\n}\n\nbutton:hover, input:hover[type=\"submit\"]:hover,\nbutton:focus, input:focus[type=\"submit\"]:focus,\n.btn-default:hover,\n.form-submit input:hover{\n	background-color: #FFC7F5;\n  	border: 1px solid #FFC7F5;\n}\ntextarea{\n	border: 1px solid #FFFFFF;\n}\nlabel{\n	color: #FFC7F5;\n}\ninput[type=\"text\"]:focus,\ninput[type=\"email\"]:focus,\ninput[type=\"password\"]:focus{\n	border: 1px solid #FFC7F5;\n	box-shadow: 0px 1px 2px #096484;\n}\n\n/************* header.less */\n\nheader#header h1{\n			margin-top: 10px;\n}\n	header#header h1 a{\n				color: #FF3CDE;\n	}\n				header#header h1 a:hover{\n					color: #FFC7F5;\n				}\n	header#header h2 a{\n				color: #FF3CDE;\n	}\n				header#header h2 a:hover{\n					color: #FFC7F5;\n				}\n\n/************* nav.less */\n\nnav#top-menu.navbar-default{\n			background-color: #EEE;\n		}\n			nav#top-menu.navbar-default .topSocials a{\n					color: #FF90F4;\n				}\n					nav#top-menu.navbar-default .topSocials a:hover{\n						color: #FF3CDE;\n					}\n\n	nav.navbar-default{\n		background-color: #FFC7F5;\n	}\n		nav.navbar-default .navbar-nav > li > a{			\n			/*text-shadow: #000 0px 0px 1px,   #000 0px 0px 1px,   #000 0px 0px 1px,\n             #000 0px 0px 1px,   #000 0px 0px 1px,   #000 0px 0px 1px;*/\n			color: #FFFFFF;\n		}\n		nav.navbar-default .navbar-nav > li > a:hover{\n			color: #FF3CDE;\n		}\n		nav.navbar-default .navbar-nav > .active > a{color: #FFFFFF;} \n		nav.navbar-default .navbar-nav > .active > a:hover{\n			color: #000000;\n		}\n		nav.navbar-default .navbar-nav > .active > a:focus,\n		nav.navbar-default .navbar-nav > .current-menu-ancestor > a,\n		nav.navbar-default .navbar-nav > .current-menu-ancestor > a:hover,\n		nav.navbar-default .navbar-nav > .current-menu-ancestor > a:focus,\n		nav.navbar-default .navbar-nav > .open > a,\n		nav.navbar-default .navbar-nav > .open > a:hover{\n			color: #000000;\n		} \n		nav.navbar-default .navbar-nav > .open > a:focus{\n			background-color: transparent;\n			color: #FF3CDE;\n		}\n		nav.navbar-default .dropdown-menu > li > a{\n			color: #000000;\n		}\n		nav.navbar-default .dropdown-menu > .active > a, \n		nav.navbar-default .dropdown-menu > .active > a:hover,\n		nav.navbar-default .dropdown-menu > .active > a:focus{\n			color: #FFC7F5;\n			background-color: transparent;\n		}\n\n/************* sidebar.less */\n\n\n/* Main sidebar - right */\n.sidebar{\n	background: linear-gradient(to bottom, white, #FFFFFF) repeat scroll 0 0 rgba(0, 0, 0, 0);\n	border: 1px solid #FFFFFF;\n}\n	.sidebar ul li a{\n			color: #FF3CDE;\n			}\n	.sidebar h3{\n		color: #FFC7F5;\n	}\n\n.sidebar-footer{\n	color: #FFC7F5;\n}\n\n\n/************* slider.less */\n\n.carousel .carousel-control{ /*.carousel-indicators li */\n		color: #FF9EF5;\n	}\n.carousel .carousel-caption h2 a{\n				color: #FF9EF5;\n			}\n.carousel .carousel-caption h2 a:hover{\n					color: #FF80F2;\n			}\n	.carousel .carousel-indicators li{\n			border-color: #FF9EF5;	\n		}\n			.carousel .carousel-indicators li.active{\n				background-color: #FF9EF5;\n			}\n\n/************* wp-core.less */\n\n/* TODO: Find out and style these classes*/\n.bypostauthor{color: #FFC7F5}\n.wp-caption{color: #FFC7F5}\n.wp-caption-text{color: #FFC7F5}\n.gallery-caption{color: #FFC7F5}\n\n/* End WP Core class */', 'iamsocial', '', 'publish', 'closed', 'closed', '', 'iamsocial', '', '', '2017-10-25 23:23:35', '2017-10-25 23:23:35', '', 0, 'http://localhost/sc/2017/10/17/iamsocial/', 0, 'custom_css', '', 0),
