(321, 1, '2017-10-25 21:51:57', '2017-10-25 21:51:57', '/* \n							     nuevos \n@brand-primary:         #b23702; FFC7F5\n@brand-secondary:		#ff4d00; FAD6F4\n@brand-complementary: 	#1fc908; FF11E6\n@brand-complementary-secondary: #7eff0d; FF80F2\n@gray-brand: 			#e5e5e5;\n\n@gray-lighter:           lighten(@gray-base, 93.5%); // #eee\n*/\n\n/************* styles.less */\n\nh1, h2, h3, h4, h5, h6{color: #FFC7F5;}\n\narticle,\n#respond,\n.pingback .comment-body, .trackback .comment-body{ \n	background: linear-gradient(to bottom, white, #e5e5e5) repeat scroll 0 0 rgba(0, 0, 0, 0);\n  	border: 1px solid #e5e5e5;\n}\n	article a,\n	#respond a,\n	.pingback .comment-body a, .trackback .comment-body a{\n		color: #FAD6F4;\n	}\n	article a:hover,\n	#respond a:hover,\n	.pingback .comment-body a:hover, .trackback .comment-body a:hover{\n		color: #FFC7F5;\n		}\n	article small a,\n	#respond small a{\n			color: #FF11E6;\n	}\n	article small a:hover,\n	#respond small a:hover{\n				color:  #FF80F2;\n			}\n\n	article nav small a,\n	#respond nav small a{\n				color:#FFC7F5;\n	}\n	article nav small a:hover,\n	#respond nav small a:hover{\n					color:  #FAD6F4;\n				}\n	body.blog article small.readmore a, \n	body.archive article small.readmore a, \n	body.search article small.readmore a{\n		color: #FAD6F4;\n	}\n\n	code{color: #157800;}\n\n	/*Sticky*/\n	section#sticky  a,\n	article.sticky  a{\n		color: #FF11E6; /*@brand-complementary: 	#FF11E6;*/\n	}\n\n	section#sticky a:hover,\n	article.sticky  a:hover{\n		color: #FF80F2; /*@brand-complementary-secondary:*/\n	}\n\n	/*Sticky post*/\n	section#sticky .metas small a,	\n	article.sticky .metas small a{\n					color: #FAD6F4; /*@brand-secondary: 	#FF11E6;*/\n	}\n	section#sticky .metas small a:hover,\n	section#sticky .metas small a:focus,\n	article.sticky .metas small a:hover,\n	article.sticky .metas small a:focus{\n		color: #FFC7F5; /*@brand-primary: 	#FF11E6;*/\n	}\n\n	body.blog article.sticky small.readmore a, \n	body.archive article.sticky small.readmore a, \n	body.search article.sticky small.readmore a{\n		color: #FF11E6; /*@brand-complementary: 	#FF11E6;*/\n	}\n\n/************* footer.less */\n\nfooter#pageFooter{\n	background-color: #eee;\n}\n	footer#pageFooter a{\n		color: #FAD6F4;\n	}\n\n/************* form.less */\n\nbutton:not([type=\"image\"]), input[type=\"submit\"]:not([type=\"image\"]),\n.btn-default,\n.form-submit input{\n	background-color: #FAD6F4;\n  	border: 1px solid #FAD6F4;\n	color: #e5e5e5;\n}\n\nbutton:hover, input:hover[type=\"submit\"]:hover,\nbutton:focus, input:focus[type=\"submit\"]:focus,\n.btn-default:hover,\n.form-submit input:hover{\n	background-color: #FFC7F5;\n  	border: 1px solid #FFC7F5;\n}\ntextarea{\n	border: 1px solid #e5e5e5;\n}\nlabel{\n	color: #FFC7F5;\n}\ninput[type=\"text\"]:focus,\ninput[type=\"email\"]:focus,\ninput[type=\"password\"]:focus{\n	border: 1px solid #FFC7F5;\n	box-shadow: 0px 1px 2px #096484;\n}\n\n/************* header.less */\n\nheader#header h1{\n			margin-top: 10px;\n}\n	header#header h1 a{\n				color: #FAD6F4;\n	}\n				header#header h1 a:hover{\n					color: #FFC7F5;\n				}\n	header#header h2 a{\n				color: #FAD6F4;\n	}\n				header#header h2 a:hover{\n					color: #FFC7F5;\n				}\n\n/************* nav.less */\n\nnav#top-menu.navbar-default{\n			background-color: #EEE;\n		}\n			nav#top-menu.navbar-default .topSocials a{\n					color: #FFC7F5;\n				}\n					nav#top-menu.navbar-default .topSocials a:hover{\n						color: #FAD6F4;\n					}\n\n	nav.navbar-default{\n		background-color: #FFC7F5;\n	}\n		nav.navbar-default .navbar-nav > li > a{\n			color: #e5e5e5;\n		}\n		nav.navbar-default .navbar-nav > li > a:hover{\n			color: #FAD6F4;\n		}\n		nav.navbar-default .navbar-nav > .active > a, \n		nav.navbar-default .navbar-nav > .active > a:hover, \n		nav.navbar-default .navbar-nav > .active > a:focus,\n		nav.navbar-default .navbar-nav > .current-menu-ancestor > a,\n		nav.navbar-default .navbar-nav > .current-menu-ancestor > a:hover,\n		nav.navbar-default .navbar-nav > .current-menu-ancestor > a:focus,\n		nav.navbar-default .navbar-nav > .open > a, \n		nav.navbar-default .navbar-nav > .open > a:hover, \n		nav.navbar-default .navbar-nav > .open > a:focus{\n			background-color: transparent;\n			color: #FAD6F4;\n		}\n		nav.navbar-default .dropdown-menu > li > a{\n			color: #FFC7F5;\n		}\n		nav.navbar-default .dropdown-menu > .active > a, \n		nav.navbar-default .dropdown-menu > .active > a:hover, \n		nav.navbar-default .dropdown-menu > .active > a:focus{\n			color: #FFC7F5;\n			background-color: transparent;\n		}\n\n/************* sidebar.less */\n\n\n/* Main sidebar - right */\n.sidebar{\n	background: linear-gradient(to bottom, white, #e5e5e5) repeat scroll 0 0 rgba(0, 0, 0, 0);\n	border: 1px solid #e5e5e5;\n}\n	.sidebar ul li a{\n			color: #FAD6F4;\n			}\n	.sidebar h3{\n		color: #FFC7F5;\n	}\n\n.sidebar-footer{\n	color: #FFC7F5;\n}\n\n\n/************* slider.less */\n\n.carousel .carousel-control{ /*.carousel-indicators li */\n		color: #FF11E6;\n	}\n.carousel .carousel-caption h2 a{\n				color: #FF11E6;\n			}\n.carousel .carousel-caption h2 a:hover{\n					color: #FF80F2;\n			}\n	.carousel .carousel-indicators li{\n			border-color: #FF11E6;	\n		}\n			.carousel .carousel-indicators li.active{\n				background-color: #FF11E6;\n			}\n\n/************* wp-core.less */\n\n/* TODO: Find out and style these classes*/\n.bypostauthor{color: #FFC7F5}\n.wp-caption{color: #FFC7F5}\n.wp-caption-text{color: #FFC7F5}\n.gallery-caption{color: #FFC7F5}\n\n/* End WP Core class */', 'iamsocial', '', 'inherit', 'closed', 'closed', '', '223-revision-v1', '', '', '2017-10-25 21:51:57', '2017-10-25 21:51:57', '', 223, 'http://localhost/sc/2017/10/25/223-revision-v1/', 0, 'revision', '', 0),
